function addLoadEvent(func){var oldonload=window.onload;if(typeof window.onload!="function"){window.onload=func}else{window.onload=function(){oldonload();func()}}}function insertAfter(newElement,targetElement){var parent=targetElement.parentNode;if(parent.lastChild==targetElement){parent.appendChild(newElement)}else{parent.insertBefore(newElement,targetElement.nextSibling)}}function addClass(element,value){if(!element.className){element.className=value}else{var newClassName=element.className+" "+value;element.className=newClassName}}function moveElement(elementId,final_x,final_y,interval){if(!document.getElementById){return false}if(!document.getElementById(elementId)){return false}var elem=document.getElementById(elementId);if(elem.movement){clearTimeout(elem.movement)}if(!elem.style.left){elem.style.left="0px"}if(!elem.style.top){elem.style.top="0px"}var xpos=parseInt(elem.style.left);var ypos=parseInt(elem.style.top);if(xpos==final_x&&ypos==final_y){return true}if(xpos<final_x){var dist=Math.ceil((final_x-xpos)/10);xpos+=dist}if(xpos>final_x){var dist=Math.ceil((xpos-final_x)/10);xpos-=dist}if(ypos<final_y){var dist=Math.ceil((final_y-ypos)/10);ypos+=dist}if(ypos>final_y){var dist=Math.ceil((ypos-final_y)/10)}elem.style.left=xpos+"px";elem.style.top=ypos+"px";var repeat="moveElement('"+elementId+"',"+final_x+","+final_y+","+interval+")";elem.movement=setTimeout(repeat,interval)}function highLightPage(){if(!document.getElementsByTagName){return false}var header=document.getElementsByTagName("header");if(header.length==0){return false}var nav=header[0].getElementsByTagName("nav");if(nav.length==0){return false}var links=nav[0].getElementsByTagName("a");var linkurl;for(var i=0;i<links.length;i++){linkurl=links[i].getAttribute("href");if(window.location.href.indexOf(linkurl)!=-1){addClass(links[i],"here");var linktext=links[i].lastChild.nodeValue.toLowerCase();document.body.setAttribute("id",linktext)}}}addLoadEvent(highLightPage);function prepareSlideshow(){if(!document.getElementsByTagName){return false}if(!document.getElementById){return false}if(!document.getElementById("intro")){return false}var intro=document.getElementById("intro");var slideshow=document.createElement("div");slideshow.setAttribute("id","slideshow");var preview=document.createElement("img");preview.setAttribute("id","preview");preview.setAttribute("src","images/slideshow.gif");preview.setAttribute("alt","a glimpse of what awaits you");slideshow.appendChild(preview);insertAfter(slideshow,intro);var frame=document.createElement("img");frame.setAttribute("id","frame");frame.setAttribute("alt"," ");frame.setAttribute("src","images/frame.gif");slideshow.appendChild(frame);var links=intro.getElementsByTagName("a");var destination;for(var i=0;i<links.length;i++){links[i].onmouseover=function(){destination=this.getAttribute("href");if(destination.indexOf("index.html")!=-1){moveElement("preview",0,0,5)}if(destination.indexOf("about.html")!=-1){moveElement("preview",-150,0,5)}if(destination.indexOf("photos.html")!=-1){moveElement("preview",-300,0,5)}if(destination.indexOf("live.html")!=-1){moveElement("preview",-450,0,5)}if(destination.indexOf("contact.html")!=-1){moveElement("preview",-600,0,5)}}}}addLoadEvent(prepareSlideshow);function showSection(id){var sections=document.getElementsByTagName("section");for(var i=0;i<sections.length;i++){if(sections[i].getAttribute("id")==id){sections[i].style.display="block"}else{sections[i].style.display="none"}}}function prepareInternalnav(){if(!document.getElementsByTagName){return false}if(!document.getElementById){return false}var articles=document.getElementsByTagName("article");if(articles.length==0){return false}var navs=articles[0].getElementsByTagName("nav");if(navs.length==0){return false}var links=navs[0].getElementsByTagName("a");for(var i=0;i<links.length;i++){var sectionId=links[i].getAttribute("href").split("#")[1];if(!document.getElementById(sectionId)){continue}document.getElementById(sectionId).style.display="none";links[i].destination=sectionId;links[i].onclick=function(){showSection(this.destination);return false}}}addLoadEvent(prepareInternalnav);function preparePlaceholder(){if(!document.createElement){return false}if(!document.createTextNode){return false}if(!document.getElementById){return false}if(!document.getElementById("imagegallery")){return false}var placeholder=document.createElement("img");placeholder.setAttribute("id","placeholder");placeholder.setAttribute("src","images/placeholder.gif");placeholder.setAttribute("alt","my image gallery");var description=document.createElement("p");description.setAttribute("id","description");var desctext=document.createTextNode("please choose a picture");description.appendChild(desctext);var imagegallery=document.getElementById("imagegallery");insertAfter(description,imagegallery);insertAfter(placeholder,description)}function showPic(whichpic){if(!document.getElementById("placeholder")){return false}var placeholder=document.getElementById("placeholder");
if(!document.getElementById("description")){return false}var description=document.getElementById("description");var source=whichpic.getAttribute("href");placeholder.setAttribute("src",source);if(whichpic.getAttribute("title")){var text=whichpic.getAttribute("title")}else{var text=""}if(description.firstChild.nodeType==3){description.firstChild.nodeValue=text}return false}function prepareGallery(){if(!document.getElementById){return false}if(!document.getElementsByTagName){return false}if(!document.getElementById("imagegallery")){return false}var gallery=document.getElementById("imagegallery");var links=gallery.getElementsByTagName("a");for(var i=0;i<links.length;i++){links[i].onclick=function(){return showPic(this)}}}addLoadEvent(preparePlaceholder);addLoadEvent(prepareGallery);function stripeTable(){if(!document.getElementsByTagName){return false}var tables=document.getElementsByTagName("table");for(var i=0;i<tables.length;i++){var odd=false;var trs=tables[i].getElementsByTagName("tr");for(var j=0;j<trs.length;j++){if(odd==true){addClass(trs[j],"odd");odd=false}else{odd=true}}}}function highlightRows(){if(!document.getElementsByTagName){return false}var rows=document.getElementsByTagName("tr");for(var i=0;i<rows.length;i++){rows[i].oldClassName=rows[i].className;rows[i].onmouseover=function(){addClass(this,"highlight")};rows[i].onmouseout=function(){this.className=this.oldClassName}}}function displayAbbreviations(){if(!document.createElement){return false}if(!document.createTextNode){return false}if(!document.getElementsByTagName){return false}var abbreviations=document.getElementsByTagName("abbr");if(abbreviations.length==0){return false}var array=new Array();for(var i=0;i<abbreviations.length;i++){if(abbreviations[i].childNodes.length==0){continue}var key=abbreviations[i].firstChild.nodeValue;var definition=abbreviations[i].getAttribute("title");array[key]=definition}var dlist=document.createElement("dl");for(key in array){var definition=array[key];var dtitle=document.createElement("dt");var dtitle_text=document.createTextNode(key);dtitle.appendChild(dtitle_text);var ddesc=document.createElement("dd");var ddesc_text=document.createTextNode(definition);ddesc.appendChild(ddesc_text);dlist.appendChild(dtitle);dlist.appendChild(ddesc)}var header=document.createElement("h3");var header_text=document.createTextNode("Abbreviations");header.appendChild(header_text);var articles=document.getElementsByTagName("article");var container=articles[0];container.appendChild(header);container.appendChild(dlist)}addLoadEvent(stripeTable);addLoadEvent(highlightRows);addLoadEvent(displayAbbreviations);function focusLabels(){if(!document.getElementsByTagName){return false}var labels=document.getElementsByTagName("label");for(var i=0;i<labels.length;i++){if(!labels[i].getAttribute("for")){continue}labels[i].onclick=function(){var id=this.getAttribute("for");if(!document.getElementById(id)){return false}var elem=document.getElementById(id);elem.focus()}}}addLoadEvent(focusLabels);function resetFields(whichform){if(!Modernizr.input.placeholder){return}for(var i=0;i<whichform.elements.length;i++){var element=whichform.elements[i];if(element.type=="submit"){continue}var check=element.placeholder||element.getAttribute("placeholder");if(!check){continue}element.onfocus=function(){var text=this.placeholder||this.getAttribute("placeholder");if(this.value==text){this.className="";this.value=""}};element.onblur=function(){if(this.value==""){this.className="placeholder";this.value=this.placeholder||this.getAttribute("placeholder")}};element.onblur()}}function isFilled(field){if(field.value.replace(" "," ").length<=0){return false}var placeholder=field.placeholder||field.getAttribute("placeholder");return(field.value!=placeholder)}function isEmail(field){return(field.value.indexOf("@")!=-1&&field.value.indexOf(".")!=-1)}function validateForm(whichform){for(var i=0;i<whichform.elements.length;i++){var element=whichform.elements[i];if(element.required){if(!isFilled(element)){alert("please fill in the "+element.name+"field.");return false}}if(element.type=="email"){if(!isEmail(element)){alert("The"+element.name+"field must be a valid email address");return false}}}return true}function prepareForms(){for(var j=0;j<document.forms.length;j++){var thisForm=document.forms[j];resetFields(thisForm);thisForm.onsubmit=function(){if(!validateForm(this)){return false}var article=document.getElementsByTagName("article")[0];if(submitFormWithAjax(this,article)){return false}return true}}}addLoadEvent(prepareForms);function getHTTPObject(){if(typeof XMLHttpRequest=="undefined"){XMLHttpRequest=function(){try{return new ActiveXObject("Msxml2.XMLHTTP.6.0")}catch(e){}try{return new ActiveXObject("Msxml2.XMLHTTP.3.0")}catch(e){}try{return new ActiveXObject("Msxml2.XMLHTTP")}catch(e){}return false}}return new XMLHttpRequest()}function displayAjaxLoading(element){while(element.hasChildNodes()){element.removeChild(element.lastChild)}var content=document.createElement("img");
content.setAttribute("src","images/loader.gif");content.setAttribute("alt","loading......");element.appendChild(content)}function submitFormWithAjax(whichform,thetarget){var request=getHTTPObject();if(!request){return false}displayAjaxLoading(thetarget);var dataParts=[];var element;for(var i=0;i<whichform.elements.length;i++){element=whichform.elements[i];dataParts[i]=element.name+"="+encodeURIComponent(element.value)}var data=dataParts.join("&");request.open("post",whichform.getAttribute("action"),true);request.setRequestHeader("Content-type","application/x-www-form-urlencoded");request.onreadystatechange=function(){if(request.readyState==4){if(request.status==200||request.status==0){var matchs=request.responseText.match(/<article>([\s\S]+)<\/article>/);if(matchs.length){thetarget.innerHTML=matchs[1]}else{thetarget.innerHTML="<p>Oops,there was an error,sorry.</p>"}}else{thetarget.innerHTML="<P>"+request.statusText+"</p>"}}};if(!request.send(data)){return false}return true};